import './ItemListContainer.css'
import { useState, useEffect } from 'react'
export const ItemListContainer = ({greeting}) => {

  const [nombre, setNombre] = useState("pepito")
  const [contador, setContador] = useState(0)

  const cambiarContador = ()=>{
    setContador(contador +1)
  }


  const cambiarNombreMaria = ()=>{
    setNombre("maria")
  }
  const cambiarNombrePepito = () =>{
    setNombre("pepito")

  }
  useEffect(() =>{
    console.log("me ejecute solo una vez")
  }, []) // arreglo de dependencias vacio
  useEffect(() =>{
    console.log("me ejecute cada vez que cambia algo")
  }, [contador]) // arreglo de dependencias de una variable
  console.log("me ejecute fuera del efecto")

  return (
    <div>
      <h1>Hola {nombre} cómo estás?</h1>
      <button onClick={cambiarNombreMaria}>Cambiar a María</button>
      <button onClick={cambiarNombrePepito}>Cambiar a Pepito</button>
      <button onClick={cambiarContador}>contador</button>
      <h2 className='titulo'>{greeting}</h2>
    </div>
  )
}
